import { IStore } from '../../redux/store/types';
import {setAddFieldValue} from "../../redux/action-creators/action-creators";
import { connect } from 'tls';
import { Component } from "./component";

export const mapStateToProps = (store: IStore) =>({
    fieldValue: store.addField,
});

export const mapDispatchToProps = {
    setAddFieldValue: setAddFieldValue,
};

export const ConnectedItemAddForm = connect(mapStateToProps, mapDispatchToProps)(Component);
